#include <cstdio>
int yy[]{1,0,-1,0},xx[]{0,1,0,-1},n,r;char v[6][6];bool dfs(int y,int x){int ny,nx;for(int d=0;d<4;d++){ny=y,nx=x;while(1){ny+=yy[d],nx+=xx[d];if(ny<0||ny>=n||nx<0||nx>=n)break;if(v[ny][nx]=='S')return 1;if(v[ny][nx]=='O')break;}}return 0;}void clc(){for(int y=0;y<n;y++)for(int x=0;x<n;x++)if(v[y][x]=='T'&&dfs(y,x))return;r=1;}void btr(int m,int c){if(!c){clc();return;}if(r||m==n*n)return;for(int i=m/n;!r&&i<n;i++)for(int j=i==m/n?m%n:0;!r&&j<n;j++)if(v[i][j]=='X'){v[i][j]='O';btr(i*j+1,c-1);v[i][j]='X';}}int main(){scanf("%d\n",&n);for(int y=0;y<n;y++)for(int x=0;x<n;x++)scanf("%c ",&v[y][x]);btr(0,3);printf("%s",r?"YES":"NO");}
